package com.mingda.action.implemention;

import java.io.IOException;
import java.io.OutputStream;
import java.io.UnsupportedEncodingException;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;

import org.apache.log4j.Logger;
import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;
import org.hibernate.Session;
import org.hibernate.Transaction;

import com.mingda.common.SessionFactory;
import com.mingda.common.implemention.BillmonthHandle;

/**
 * MyEclipse Struts Creation date: 11-20-2008
 * 
 * XDoclet definition:
 * 
 * @struts.action validate="true"
 */

public class BillcreatefileAction extends Action {
	/*
	 * Generated Methods
	 */

	/**
	 * Method execute
	 * 
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	static Logger log = Logger.getLogger(BillcreatefileAction.class);

	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		String monthid = request.getParameter("monthid");
		String defineid = request.getParameter("defineid");
		String orgid = request.getParameter("orgid");
		String type = request.getParameter("type");
		String typename = request.getParameter("typename");
		String orgname = request.getParameter("orgname");
		try {
			if (null == type || "".equals(type)) {
				typename = "нд╪Ч";
			} else {
				typename = new String(typename.getBytes("iso8859-1"), "GB18030");
			}
			if (null == orgname || "".equals(orgname)) {
				orgname = "";
			} else {
				orgname = new String(orgname.getBytes("iso8859-1"), "GB18030");
			}
		} catch (UnsupportedEncodingException e) {
			e.printStackTrace();
		}

		log.debug(type + "    " + orgid + "  " + monthid + "     " + defineid);
		Session session = SessionFactory.getSession();
		String targetfile = orgname + typename + ".txt";
		Transaction tx = session.beginTransaction();
		try {
			tx.begin();
			BillmonthHandle bmh = new BillmonthHandle();

			OutputStream os = response.getOutputStream();
			response.reset();
			response.setContentType("APPLICATION/OCTET-STREAM");
			response.setHeader("Content-Disposition", "attachment; filename=\""
					+ new String(targetfile.getBytes("GBK"), "iso8859-1")
					+ "\"");
			bmh.createBillFile(os, monthid, defineid, orgid, type);
			os.close();
			tx.commit();
		} catch (IOException e) {
			tx.rollback();
			e.printStackTrace();
		} catch (Exception e) {
			tx.rollback();
			e.printStackTrace();
		} finally {
			SessionFactory.closeSession();
		}

		return null;
	}
}