package com.mingda.action.implemention;

import java.sql.SQLException;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.servlet.http.HttpSession;

import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;
import org.hibernate.Session;
import org.hibernate.Transaction;

import com.mingda.common.SessionFactory;
import com.mingda.common.implemention.BillmonthHandle;
import com.mingda.entity.SysTEmployee;

/**
 * MyEclipse Struts Creation date: 11-17-2008
 * 
 * XDoclet definition:
 * 
 * @struts.action validate="true"
 */
public class BillmonthdelAction extends Action {
	/*
	 * Generated Methods
	 */

	/**
	 * Method execute
	 * 
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		HttpSession httpsession = request.getSession();
		SysTEmployee employee = (SysTEmployee) httpsession
				.getAttribute("employee");
		String monthid = request.getParameter("monthid");
		Session session = SessionFactory.getSession();
		Transaction tx = session.beginTransaction();
		BillmonthHandle bh = new BillmonthHandle();
		try {
			
			if (bh.isDeleteMonth(employee.getSysTOrganization()
					.getOrganizationId())) {
				bh.delMonth(monthid);
			}else{
				request.setAttribute("delstr", "变动信息不为空，不可以进行删除操作");
			}
			tx.commit();
		} catch (SQLException e) {
			tx.rollback();
			e.printStackTrace();
		} finally {
			session.close();
		}
		ActionForward forward = new ActionForward(
				"/page/implemention/billcreateinit.do");
		return forward;
	}
}