package com.mingda.action.info.editor;

import java.io.File;
import java.io.IOException;
import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.SQLException;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;

import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;
import org.hibernate.Session;
import org.hibernate.Transaction;

import com.mingda.common.ConstantDefine;
import com.mingda.common.SessionFactory;
import com.mingda.common.UploadFile;
import com.mingda.common.log4j.Log4jApp;
import com.mingda.form.UploadForm;

/**
 * MyEclipse Struts Creation date: 07-04-2008
 * 
 * XDoclet definition:
 * 
 * @struts.action validate="true"
 */
public class InchsaveAction extends Action {
	/*
	 * Generated Methods
	 */

	/**
	 * Method execute
	 * 
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	@SuppressWarnings("deprecation")
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		UploadForm uplaodForm = (UploadForm) form;
		String id = request.getParameter("id");
		String filename = "";
		if (uplaodForm.getFile() != null
				&& !uplaodForm.getFile().getFileName().equals("")) {
			UploadFile uf = new UploadFile();
			String path = "";
			try {
				// linux£ºÄ¿Â¼ "/"
				//É¾³ýÎÄ¼þ¼Ð
				path = "/" + id.trim() + "/";
				File file = new File(ConstantDefine.UPLOADPATH + "/" + path);
				if (file.isDirectory()) {
					String[] filelist = file.list();
					for (int i = 0; i < filelist.length; i++) {
						File readfile = new File(file.getPath() + "\\"
								+ filelist[i]);
						readfile.delete();
					}
				}
				//
				filename = uf.upload(uplaodForm.getFile(), path.trim(),
						java.util.UUID.randomUUID().toString());
				Log4jApp.logger(filename.trim());
				Session session = SessionFactory.getSession();
				Transaction tx = session.beginTransaction();
				Connection conn = session.connection();
				filename = path + filename;
				String sql = "update info_t_member t set t.picpath ='"
						+filename.trim() + "' where t.member_id =" + id.trim();
				PreparedStatement ps = conn.prepareStatement(sql);
				Log4jApp.logger(sql);
				ps.execute();
				ps.close();
				tx.commit();
				conn.close();
				session.close();
			} catch (IOException e1) {
				e1.printStackTrace();
			} catch (SQLException e) {
				e.printStackTrace();
			}
		}
		ActionForward forward = new ActionForward(
				"/page/info/editor/inch.jsp?memberId=" + id + "&filename="
						+ filename.trim());
		return (forward);
	}
}