package com.mingda.action.info.synthesis;

import java.io.IOException;
import java.io.PrintWriter;
import java.math.BigDecimal;
import java.util.List;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;

import org.apache.log4j.Logger;
import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;
import org.dom4j.Document;
import org.dom4j.Element;
/**
 * MyEclipse Struts Creation date: 12-16-2008
 * 
 * XDoclet definition:
 * 
 * @struts.action validate="true"
 */
public class FindtablechildAction extends Action {
	static Logger log = Logger.getLogger(FindtablechildAction.class);
	/*
	 * Generated Methods
	 */

	/**
	 * Method execute
	 * 
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		String tablename=request.getParameter("tablename");
		String dep =request.getParameter("dep");
		BigDecimal b =new BigDecimal (dep);
		int m = b.intValue() +1;
		Document treexml = (Document) this.servlet.getServletContext()
				.getAttribute("tree");
		log.debug(tablename);
		Element family = (Element) treexml.getRootElement().selectSingleNode("//"+tablename+"");
		log.debug(family.asXML());
		List list = family.elements();
		String tempstr = "";
		for (int i = 0; i < list.size(); i++) {
			Element element = (Element) list.get(i);
			if ("property".equals(element.getName())) {
				tempstr = tempstr
						+ "<div style=\"padding-left:"+m*5+"px;\" id =\""
						+ element.attributeValue("column")
						+ "\" ><img alt=0 src=\"../../images/minus.png\"/>&nbsp;<span onclick=\"edit('"
						+ element.attributeValue("column")
						+ "','"
						+ element.getParent().attributeValue("table")
						+ "','"
						+ element.getParent().getParent().attributeValue(
								"table") + "')\">"
						+ element.attributeValue("title") + "</span></div>";
			} else {
				tempstr = tempstr
						+ "<div  style=\"padding-left:"+m*5+"px;\" id =\""
						+ element.attributeValue("table")
						+ "\" ><img alt=1 src=\"../../images/plus.png\" onclick=\"readchild(this,'"
						+ element.getName() + "' ,'"+m+"')\"/>&nbsp;"
						+ element.attributeValue("title")
						+ "</div><div style=\"padding-left:"+m*5+"px;\" id =\"C_"
						+ element.getName() + "\" style=\"display:none\"></div>";
			}
		}
		request.setAttribute("html", tempstr);
		response.setCharacterEncoding("GB18030");
		PrintWriter out;
		try {
			out = response.getWriter();
			out.println(tempstr);
		} catch (IOException e) {
			e.printStackTrace();
		}
		return null;
	}
}