package com.mingda.action.oa;

import java.util.List;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;

import com.mingda.dao.NetTFileDAO;
import com.mingda.dao.NetTNoticeDAO;
import com.mingda.entity.NetTFile;
import com.mingda.entity.NetTNotice;

/**
 * MyEclipse Struts Creation date: 12-26-2008
 * 
 * XDoclet definition:
 * 
 * @struts.action validate="true"
 * @struts.action-forward name="announcementbrowser"
 *                        path="/page/oa/announcementbrowser.jsp"
 */
public class AnnouncmentbrowserAction extends Action {
	/*
	 * Generated Methods
	 */

	/**
	 * Method execute
	 * 
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		String relid = request.getParameter("relid");
		String reltable = request.getParameter("reltable");
		if (null != relid) {

			NetTNoticeDAO noticedao = new NetTNoticeDAO();
			NetTNotice notice = noticedao.findById(new Long(relid));
			request.setAttribute("notice", notice);
			request.setAttribute("reces", notice.getNetTReceives());
			NetTFileDAO filedao = new NetTFileDAO();
			List<NetTFile> netTFiles = filedao.findByProperty(reltable, relid);
			request.setAttribute("filelist", netTFiles);
		}
		return mapping.findForward("announcementbrowser");
	}
}