package com.mingda.action.oa;

import java.io.IOException;
import java.io.PrintWriter;
import java.io.UnsupportedEncodingException;
import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;

import org.apache.log4j.Logger;
import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;
import org.hibernate.HibernateException;
import org.hibernate.Transaction;
import com.mingda.common.SessionFactory;

/**
 * MyEclipse Struts Creation date: 09-03-2008
 * 
 * XDoclet definition:
 * 
 * @struts.action validate="true"
 */
public class WritemsgAction extends Action {
	static Logger log = Logger.getLogger(WritemsgAction.class);
	/*
	 * Generated Methods
	 */

	/**
	 * Method execute
	 * 
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		try {
			request.setCharacterEncoding("utf-8");
		} catch (UnsupportedEncodingException e2) {
			e2.printStackTrace();
		}
		String msgid = request.getParameter("msgid");
		String location = request.getParameter("location");
		String propose = request.getParameter("propose");
		String content = request.getParameter("content");
		String optip = request.getRemoteAddr();

		String finish = "信息没有保存成功！";
		Connection con = SessionFactory.getSession().connection();
		Transaction tx = SessionFactory.getSession().beginTransaction();
		try {
			PreparedStatement ps = null;
			String sql = "";
			if (null == msgid || "".equals(msgid)) {
				sql = "select xmsgid.nextval as msgid from dual";
				ps = con.prepareStatement(sql);
				ResultSet rs = ps.executeQuery();
				if (rs.next()) {
					msgid = rs.getString("msgid");
				}
				rs.close();
				ps.close();
				sql = "insert into message"
						+ " (msgid, content, optip, location, propose) "
						+ "values " + "(" + msgid + ", '" + content + "','"
						+ optip + "','" + location + "', '" + propose + "')";
			} else {
				sql = " update message " + "set content = '" + content
						+ "',optip ='" + optip + "',location = '" + location
						+ "',propose = '" + propose + "' where msgid = "
						+ msgid;
			}
			log.debug(sql);
			ps = con.prepareStatement(sql);
			ps.execute();
			tx.commit();
			finish = "信息保存成功!";
		} catch (HibernateException e1) {
			tx.rollback();
			e1.printStackTrace();
		} catch (NumberFormatException e1) {
			tx.rollback();
			e1.printStackTrace();
		} catch (SQLException e) {
			tx.rollback();
			e.printStackTrace();
		}

		response.setContentType("text/html");
		response.setCharacterEncoding("GB18030");
		try {
			PrintWriter out = response.getWriter();
			out.write(finish);
			out.flush();
			out.close();
		} catch (IOException e) {
			e.printStackTrace();
		}

		return null;
	}
}