package com.mingda.action.paperprint;

import java.util.List;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;

import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;

import com.mingda.common.SessionFactory;
import com.mingda.common.paperprint.PaperPrint;

/**
 * MyEclipse Struts Creation date: 01-14-2009
 * 
 * XDoclet definition:
 * 
 * @struts.action validate="true"
 * @struts.action-forward name="paperprint"
 *                        path="/page/paperprint/paperprint.jsp" redirect="true"
 */
public class PaperprintAction extends Action {
	/*
	 * Generated Methods
	 */

	/**
	 * Method execute
	 * 
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		String familyId = request.getParameter("nodeId");
		if (null == familyId || "".equals(familyId)) {
			familyId = request.getParameter("fid");
		} else {

		}
		PaperPrint print = new PaperPrint();
		try {
			List<Object[]> paperlist = print.getPrintPapersByFamily(familyId);
			Object[] objs = print.getFamilyInfo(familyId);
			request.setAttribute("mastername", objs[1].toString());
			request.setAttribute("familyno", objs[0].toString());
			request.setAttribute("familyId", familyId);
			request.setAttribute("paperlist", paperlist);
		} catch (Exception e) {
			e.printStackTrace();
		} finally {

			SessionFactory.closeSession();
		}

		return mapping.findForward("paperprint");
	}
}