package com.mingda.action.paperprint;

import java.io.PrintWriter;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;

import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;
import org.hibernate.Session;
import org.hibernate.Transaction;

import com.mingda.common.SessionFactory;
import com.mingda.common.paperprint.PaperPrint;

/**
 * MyEclipse Struts Creation date: 01-20-2009
 * 
 * XDoclet definition:
 * 
 * @struts.action validate="true"
 */
public class PaperprintredohandleAction extends Action {
	/*
	 * Generated Methods
	 */

	/**
	 * Method execute
	 * 
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		String prcid = request.getParameter("prcid");
		String fid = request.getParameter("fid");
		String ptid = request.getParameter("ptid");
		
		Session session =SessionFactory.getSession();
		Transaction tx =session.beginTransaction();
		try{
			PaperPrint paperprint =new PaperPrint();
			paperprint.redoPaperHandle(prcid);
			tx.commit();
			PrintWriter out = response.getWriter();
			String htmlstr = "<link rel=\"stylesheet\" href=\"../css/style.css\" type=\"text/css\"></link><p></p><p align=\"center\">此证件已经作废</p><p align=\"center\">"
				+ "<button class=\"bnt\" onclick=\"javascript:window.close();window.opener.close()\">关闭</button>"
				+ "</p>";
			out.println(htmlstr);
			out.flush();
			out.close();
		}catch(Exception e){
			tx.rollback();
			e.printStackTrace();
		}
		
		return null;
	}
}