public class PrintpaperbrowseAction extends Action {
	/*
	 * Generated Methods
	 */

	/**
	 * Method execute
	 * 
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		String ptid = request.getParameter("ptid");
		String fid = request.getParameter("fid");
		try {

			ImplTPaperrecord implTPaperrecord = new ImplTPaperrecord();
			implTPaperrecord.setFamilyId(new Long(fid));

			ImplTPapertype implTPapertype = new ImplTPapertype();
			implTPapertype.setPapertypeId(new Long(ptid));

			implTPaperrecord.setImplTPapertype(implTPapertype);

			PaperPrint paperprint = new PaperPrint();
			List<ImplTPaperrecord> list= paperprint.getPaperPrintBrowseByFamilyId(implTPaperrecord);
			request.setAttribute("rlist", list);
			
			Object[] objs = paperprint.getFamilyInfo(fid);
			request.setAttribute("mastername", objs[1].toString());
			request.setAttribute("familyno", objs[0].toString());
			
		} catch (Exception e) {
			e.printStackTrace();
		} finally {
			SessionFactory.closeSession();
		}
		return mapping.findForward("paperprintbrowse");
	}
}