package com.mingda.action.policy.demurral;

import java.math.BigDecimal;
import java.util.Date;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.servlet.http.HttpSession;

import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;
import org.hibernate.Session;
import org.hibernate.Transaction;

import com.mingda.common.SessionFactory;
import com.mingda.dao.BizTDemurralDAO;
import com.mingda.entity.BizTDemurral;
import com.mingda.entity.BizTFamilystatus;
import com.mingda.entity.SysTEmployee;

/**
 * MyEclipse Struts Creation date: 03-06-2009
 * 
 * XDoclet definition:
 * 
 * @struts.action validate="true"
 * @struts.action-forward name="demurralview"
 *                        path="/page/policy/demurral/demurralview.jsp"
 */
public class DemurralsaveAction extends Action {
	/*
	 * Generated Methods
	 */

	/**
	 * Method execute
	 * 
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {

		String content = request.getParameter("content");
		String ssid = request.getParameter("ssid");
		String approve = request.getParameter("approve");

		HttpSession httpsession = (HttpSession) request.getSession();
		SysTEmployee employee = (SysTEmployee) httpsession
				.getAttribute("employee");

		Session session = (Session) SessionFactory.getSession();
		Transaction tx = session.beginTransaction();

		try {

			BizTDemurralDAO bizTDemurralDAO = new BizTDemurralDAO();
			BizTDemurral bizTDemurral = new BizTDemurral();
			bizTDemurral.setApprove(approve);
			bizTDemurral.setContent(content);
			bizTDemurral.setApprovetype(employee.getSysTEmpext().getName());
			bizTDemurral.setPublisher(new BigDecimal(employee.getEmployeeId()));
			bizTDemurral.setPublishdate(new Date());
			
			BizTFamilystatus bizTFamilystatus = new BizTFamilystatus();
			bizTFamilystatus.setFamilystatusId(new BigDecimal(ssid));
			bizTDemurral.setBizTFamilystatus(bizTFamilystatus);
			
			bizTDemurralDAO.save(bizTDemurral);
			
			tx.commit();
		} catch (Exception ex) {
			tx.rollback();
			ex.printStackTrace();
		} finally {
			session.close();
		}
		ActionForward forward =new ActionForward("/page/policy/demurral/demurralview.do?ssid="+ssid);
		return forward;
	}
}