package com.mingda.action.policy.demurral;

import java.util.List;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;

import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;
import org.hibernate.Session;
import org.hibernate.Transaction;

import com.mingda.common.SessionFactory;
import com.mingda.dao.BizTDemurralDAO;
import com.mingda.entity.BizTDemurral;

/**
 * MyEclipse Struts Creation date: 03-06-2009
 * 
 * XDoclet definition:
 * 
 * @struts.action validate="true"
 * @struts.action-forward name="demurralview"
 *                        path="/page/policy/demurral/demurralview.jsp"
 */
public class DemurralviewAction extends Action {
	/*
	 * Generated Methods
	 */

	/**
	 * Method execute
	 * 
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {

		String ssid = request.getParameter("ssid");

		Session session = SessionFactory.getSession();
		Transaction tx = session.beginTransaction();
		try {
			
			BizTDemurralDAO bizTDemurralDAO = new BizTDemurralDAO();
			List<BizTDemurral> list = bizTDemurralDAO.findBySSID(ssid);

			request.setAttribute("delist", list);
			request.setAttribute("ssid", ssid);
			
			tx.commit();
		} catch (Exception ex) {
			tx.rollback();
			ex.printStackTrace();
		} finally {
			session.close();
		}

		return mapping.findForward("demurralview");
	}
}