	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		String roleId = request.getParameter("roleId");
		// roleId ="-1"; 新建角色
		Session session = com.mingda.common.SessionFactory.getSession();
		Connection con = null;
		PreparedStatement ps = null;
		ResultSet rs = null;
		String sql = "";
		String html = "<input name=\"roleId\" type=\"hidden\" value=\""+roleId+"\" />" +
				"<table width=\"100%\" border=\"0\" cellspacing=\"1\" cellpadding=\"0\"><th align=\"left\" colspan=\"2\">角色信息</th></tr>";
		try {
			sql = "select * from sys_t_role t where t.role_id ='" + roleId
					+ "'";
			con = session.connection();
			ps = con.prepareStatement(sql);
			rs = ps.executeQuery();
			if (rs.next()) {
				html += "<tr><td width=\"80\">角色名称</td><td><input name=\"name\" type=\"text\" value=\""
						+ rs.getString("name") + "\" /></td></tr>";
				html += "<tr><td>角色描述</td><td><input name=\"detail\" type=\"text\" value=\""
						+ rs.getString("detail") + "\" /></td></tr>";
			} else {
				html += "<tr><td>角色名称</td><td><input name=\"name\" type=\"text\" value=\"\" /></td></tr>";
				html += "<tr><td>角色描述</td><td><input name=\"detail\" type=\"text\" value=\"\" /></td></tr>";
			}
			html += "<tr> <td colspan=\"2\"><div class=\"dtree\"><script type=\"text/javascript\">"
					+ "d = new dTree('d');d.add(0,-1,'菜单项目');";
			sql = "select *  from sys_t_privilege a "
					+ "left join sys_t_power b on b.privilege_id = a.privilege_id "
					+ "and b.role_id = '" + roleId + "' order by a.sequence";
			ps = con.prepareStatement(sql);
			rs = ps.executeQuery();
			while (rs.next()) {
				String id = rs.getString("privilege_id");
				String pid = rs.getString("parentprivilegeid");
				String displayname = rs.getString("displayname");
				String roleid = rs.getString("role_id");
				String checked = "checked=\"checked\"";
				if (roleid == null || roleid.equals("")) {
					checked = "";
				}
				html += "d.add(" + id + "," + pid + ",'<input id=\"" + pid
						+ "\" type=\"checkbox\" name=\"item\" " + checked
						+ " value=\"" + id + "\" onclick =\"ctlchild(this , "
						+ id + "," + pid + " )\" />" + displayname + "');";
			}
			html += " document.write(d);</script></div></td></tr><table>";
		} catch (HibernateException e) {
			e.printStackTrace();
		} catch (SQLException e) {
			e.printStackTrace();
		} finally {
			try {
				if (rs != null) {
					rs.close();
				}
				if (ps != null) {
					ps.close();
				}
				if (con != null) {
					con.close();
				}
				session.close();
			} catch (SQLException e) {
			}
		}
		request.setAttribute("html", html);
		return mapping.findForward("roleright");
	}
