package com.mingda.action.system;

import java.io.IOException;
import java.io.PrintWriter;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;

import org.apache.log4j.Logger;
import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;
import org.hibernate.Session;
import org.hibernate.Transaction;

import com.mingda.common.SessionFactory;
import com.mingda.dao.SysTRoleDAO;
import com.mingda.entity.SysTRole;

/**
 * MyEclipse Struts Creation date: 08-22-2008
 * 
 * XDoclet definition:
 * 
 * @struts.action validate="true"
 */
public class RolesaveAction extends Action {
	static Logger log = Logger.getLogger(RolesaveAction.class);
	/*
	 * Generated Methods
	 */

	/**
	 * Method execute
	 * 
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		String roleId = request.getParameter("roleId");
		String status = request.getParameter("status");
		log.debug( "status    " + status);
		String bakinfo = "";
		
		if (status.equals("0")) {
			bakinfo = "<button class=\"btn\" onclick=\"changstatus("
					+ roleId + ",1)\">ª÷∏¥</button>";
		} else {
			bakinfo = "<button class=\"btn\" onclick=\"changstatus("
					+ roleId + ",0)\">Õ£”√</button>";
		}
		Session session = SessionFactory.getSession();
		SysTRoleDAO sysTRoleDAO = new SysTRoleDAO();
		SysTRole sysTRole = sysTRoleDAO.findById(new Long(roleId));
		sysTRole.setStatus(status);
		Transaction tx = session.beginTransaction();
		sysTRoleDAO.save(sysTRole);
		tx.commit();
		session.close();
		response.setContentType("text/html");
		response.setCharacterEncoding("GB18030");

		try {
			PrintWriter out = response.getWriter();
			out.write(bakinfo);
			out.flush();
			out.close();
		} catch (IOException e) {
			e.printStackTrace();
		}

		return null;
	}
}