package com.mingda.action.system.menu;

import java.util.List;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;

import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;
import org.hibernate.Session;

import com.mingda.common.SessionFactory;
import com.mingda.dao.SysTPrivilegeDAO;
import com.mingda.entity.SysTPrivilege;

/**
 * MyEclipse Struts Creation date: 09-20-2008
 * 
 * XDoclet definition:
 * 
 * @struts.action validate="true"
 */
public class MenuchildAction extends Action {
	/*
	 * Generated Methods
	 */

	/**
	 * Method execute
	 * 
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		String menuid = request.getParameter("menuid");
		Session session = SessionFactory.getSession();
		SysTPrivilegeDAO dao = new SysTPrivilegeDAO();
		SysTPrivilege privilege = dao.findById(new Long(menuid));
		List list = dao.findByParentprivilegeid(new Long(menuid));
		request.setAttribute("list", list);
		request.setAttribute("privilege", privilege);
		session.close();
		return mapping.findForward("menuchild");
	}
}